import React, { useState } from 'react';

const Form = () => {


    const [newPersona, setNewPersona] = useState ({
        nombre: '',
        apellido:'',
        edad: 0
    });

    const handleClick = e =>{
        e.preventDefault()
        if (newPersona.nombre === '' || newPersona.apellido === '' || newPersona.edad <= 0) {
            alert('todos los datos deben estar completos')
            return
        }
    };

    const handleChange = e =>{
        setNewPersona({
        ...newPersona,
            [e.target.name]: e.target.value
        })
    }

    return (
        <div className="formulario">
            <form method="POST">
                <input name="nombre" type="text" onChange={handleChange} required/>
                <input name="apellido" type="text" onChange={handleChange} required/>
                <input name="edad" type="number" onChange={handleChange} required/>
                <input type="submit" onSubmit={handleClick} />
            </form>  
        </div>
    );
};

export default Form;
