import React, { useState } from 'react';

const Form = () => {


    const [newPersona, setNewPersona] = useState ({
        nombre: '',
        apellido:'',
        edad: 0
    });

    const handleChange = e =>{
        setNewPersona({
        ...newPersona,
            [e.target.name]: e.target.value
        })
    }

    const handleClick = e =>{
        e.preventDefault()
        const requestInit = {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(newPersona)
        }
        const getPersona = async () =>{
            let save = await fetch('http://localhost:9000/api', requestInit);
            let data = await save.json();
            console.log('agregaste una persona');
        };
        getPersona();
        setNewPersona({
            nombre: '',
        apellido:'',
        edad: 0
        });
    };

    return (
        <div className="formulario">
            <form >
                <input name="nombre" type="text" onChange={handleChange} required/>
                <input name="apellido" type="text" onChange={handleChange} required/>
                <input name="edad" type="number" onChange={handleChange} min="1" pattern="^[0-9]+" required/>
                <input type="submit" onSubmit={handleClick} />
            </form>  
        </div>
    );
};

export default Form;
