import React, { useState } from 'react';

const Form = () => {


    const [newPersona, setNewPersona] = useState ({
        nombre: '',
        apellido:'',
        edad: 0
    });

    const handleChange = e =>{
        setNewPersona({
        ...newPersona,
            [e.target.name]: e.target.value
        })
        console.log(newPersona)
    }

    const handleClick = e =>{
        e.preventDefault();
        const requestInit = {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(newPersona)
        };
        fetch('http://localhost:9000/api', requestInit)
        .then(res=> res.text())
        setNewPersona({
            nombre: '',
            apellido:'',
            edad: 0
        });
        window.location.reload(true);
    };

    return (
        <div className="formulario">
            <form onSubmit={handleClick}>
                <input  name="nombre" type="text" onChange={handleChange} required placeholder=""/>
                <input  name="apellido" type="text" onChange={handleChange} required placeholder=""/>
                <input  name="edad" type="number" onChange={handleChange} min="1" pattern="^[0-9]+" required placeholder=""/>
                <input type="submit" onSubmit={handleClick}  placeholder=""/>
            </form>  
        </div>
    );
};

export default Form;
